mapper.select:
SELECT count(ID) FROM CMS_ARTICLE_ATT_FILE 
WHERE ACTICLE_ID=  #{articleID}
AND DEL_FLAG='0' AND ISPOSTARTICLE='1'

mapper.update:
UPDATE cms_article_att_file SET 
	del_flag = '1'
WHERE id = #{id}

UPDATE cms_article_att_file SET 
	del_flag = '1'
WHERE acticle_id = #{acticleid}

UPDATE cms_article_att_file SET 
ispostarticle = '1'
WHERE attfile_temp_guid = #{temp_guid}
