mapper.select:
SELECT 

FROM cms_comment a

WHERE a.id = #{id}

SELECT cc.name AS "category.name",
FROM cms_comment a
JOIN cms_category cc ON cc.id = a.category_id
c.del_flag ='0' and a.del_flag = #{delFlag}
AND a.content_id=#{contentId}
AND a.title LIKE '%'||#{title}||'%' '%'||#{title}||'%' CONCAT('%', #{title}, '%')
AND (a.category_id = #{category.id}
or cc.parent_ids like '%'||#{category.id}||'%' '%'||#{category.id}||'%' CONCAT('%,', #{category.id}, ',%') )
and a.category_id in
#{item}
ORDER BY a.create_date DESC

SELECT cc.name AS "category.name",
FROM cms_comment a
JOIN cms_category cc ON cc.id = a.category_id
c.del_flag ='0' and a.del_flag = #{comment.delFlag}
AND a.title LIKE '%'||#{comment.title}||'%'
AND (a.category_id = #{comment.category.id}
or cc.parent_ids like '%'||#{comment.category.id}||'%' )
and a.category_id in
#{item}
ORDER BY a.create_date DESC

mapper.update:
UPDATE cms_comment SET 
	category_id=#{1}

	category_id = #{0}

		AND content_id=#{2}

mapper.select:
SELECT c.title as "articletitle", cc.name AS "category.name",
FROM cms_comment a JOIN cms_article c ON c.id = a.content_id JOIN cms_category cc ON cc.id = a.category_id
c.del_flag ='0' and a.del_flag = #{delFlag}
AND a.content_id=#{contentId}
AND c.title LIKE '%'||#{title}||'%' '%'||#{title}||'%' CONCAT('%', #{title}, '%')
AND a.article_creater_id=#{articleCreaterId}
AND (a.category_id = #{category.id}
or cc.parent_ids like '%'||#{category.id}||'%' '%'||#{category.id}||'%' CONCAT('%,', #{category.id}, ',%') )
union
SELECT c.title as "articletitle", cc.name AS "category.name",
FROM cms_comment a JOIN cms_article c ON c.id = a.content_id JOIN cms_category cc ON cc.id = a.category_id
c.del_flag ='0' and a.del_flag = #{delFlag}
AND a.title LIKE '%'||#{title}||'%' '%'||#{title}||'%' CONCAT('%', #{title}, '%')
AND a.name_id=#{articleCreaterId}
AND (a.category_id = #{category.id}
or cc.parent_ids like '%'||#{category.id}||'%' '%'||#{category.id}||'%' CONCAT('%,', #{category.id}, ',%') )

mapper.update:
UPDATE cms_comment SET 
	del_flag = '3'
WHERE id = #{id}

UPDATE cms_comment SET 
	del_flag = '2'
WHERE id = #{id}

